import React, {useState} from "react";

function MessageFeed(props) {
    const [messages, setMessages] = useState({});
    setMessages(messages.push(<p className='card-text'>{props.message}</p>));


    return(
            <div className="card border-secondary mb-3 h-100 flex-grow-1 rounded-0">
                <div className="card-header rounded-0">Chat Room</div>
                <div className="card-body">
                    <p className="card-text">{messages}</p>
                </div>
            </div>
    );
}

export default MessageFeed;
